<script setup>
import { ref, onMounted, onBeforeUnmount } from 'vue';

const separatorOffset = ref(0);
const isMobile = ref(false);

function updateOffsetFromScroll(scrollTop) {
  const speed = 0.02;
  let offset = (scrollTop * speed) % 100;
  if (offset < 0) offset += 100;
  separatorOffset.value = offset;
}

onMounted(() => {
  // détection mobile (même breakpoint que ton CSS)
  isMobile.value = window.matchMedia('(max-width: 628px)').matches;

  // si mobile : pas d’animation liée au scroll
  if (isMobile.value) {
    separatorOffset.value = 0; // ou une valeur de base, genre 20 si tu veux décaler un peu
    return;
  }

  const scroller = document.querySelector('[data-scroll-container]') || window;
  if (!scroller) return;

  const handleScroll = () => {
    const scrollTop =
      scroller === window ? (window.scrollY ?? window.pageYOffset ?? 0) : scroller.scrollTop;
    updateOffsetFromScroll(scrollTop);
  };

  scroller.addEventListener('scroll', handleScroll, { passive: true });
  handleScroll(); // init

  onBeforeUnmount(() => {
    scroller.removeEventListener('scroll', handleScroll);
  });
});
</script>

